package Bpgms;

public class D24_secondBiggest {

	public static void main(String[] args) {
		int[] a={1,2,3,4,5};
		int big=0;
		for(int b:a){
			if(b>big){
				big=b;
			}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
		}
		int sbig=0;
		for (int i = 0; i < a.length; i++) {
			if(a[i]<big && a[i]>sbig){
				sbig=a[i];
			}
		}
		System.out.println(sbig);

	}

}
